
import { Entity } from "./entity";
import { Operation } from "./operation";

export type Action = string;

export function parse(action: Action): [ Entity, Operation ] {
    let segments = action.split('/');
    let operation = segments.pop();

    return [ segments.join('/'), operation ];
}

export function newAction(entity: Entity, op: Operation): Action {
    return `${entity}/${op}`;
}
